
/* Minimal jsPDF-like stub: supports new jsPDF().addImage(dataURL,'PNG',x,y,w,h); save('file.pdf') */
(function(g){
  function D(){ this.pages=[document.createElement('canvas')]; var c=this.pages[0]; c.width=595; c.height=842; this.ctx=c.getContext('2d'); this.y=20; this.font='16px sans-serif'; }
  D.prototype.text=function(t,x,y){ this.ctx.font=this.font; this.ctx.fillStyle='#000'; this.ctx.fillText(t,x,y||this.y); this.y+=20; };
  D.prototype.addImage=function(img,fmt,x,y,w,h){
    var i=new Image(); i.onload=()=>{ this.ctx.drawImage(i,x,y,w,h); }; i.src=img;
  };
  D.prototype.save=function(name){
    var a=document.createElement('a'); a.download=name||'download.pdf';
    a.href=this.pages[0].toDataURL('image/png'); // Not a real PDF but works as image "PDF" stand-in for demo
    a.click();
  };
  g.jsPDF=D;
})(window);
